package com.smile.donor.controller;

import java.util.Arrays;
import java.util.List;

import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.http.HttpStatus;
import org.springframework.http.MediaType;
import org.springframework.http.ResponseEntity;
import org.springframework.web.bind.annotation.CrossOrigin;
import org.springframework.web.bind.annotation.RequestMapping;
import org.springframework.web.bind.annotation.RequestMethod;
import org.springframework.web.bind.annotation.RestController;
import org.springframework.web.client.RestTemplate;

import com.smile.donor.model.DonorModel;

@RestController
@RequestMapping("/donor")
public class DonorController {

	@Autowired
	RestTemplate restTemplate;

	@CrossOrigin(origins = "*")
	@RequestMapping(value = "/id", method = RequestMethod.GET, produces = MediaType.APPLICATION_JSON_VALUE)
	public ResponseEntity<?> getDonor() {
		DonorModel donorModel = new DonorModel();
		String url = "data:image/jpeg;base64,";
		List<DonorModel> donors = Arrays.asList(new DonorModel(1, "food", "children Food Basket", url, 12.0),
				new DonorModel(2, "Sweet", "children Food Basket", url, 9.0));
		return new ResponseEntity<>(donors, HttpStatus.OK);

	}

}
